export default {
  name: "Profile",
  data: function data() {
    return {
      form: {},
      user: localStorage.getItem("user") ? JSON.parse(localStorage.getItem("user")) : {}
    };
  },
  created: function created() {
    var _this = this;

    this.request.get("/user/username/" + this.user.username).then(function (res) {
      if (res.code === '200') {
        _this.form = res.data;
      }
    });
  },
  method: {
    save: function save() {
      var _this2 = this;

      this.request.post("/user", this.form).then(function (res) {
        if (res.data) {
          _this2.$message.success("Save successfully!");

          _this2.dialogFormVisible = false;

          _this2.load();
        } else {
          _this2.$message.error("Fail to save!");
        }
      });
    }
  }
};
